import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminPanel.css";
import logo from "../images/krystal.png";

const AdminPanel = () => {
  const [quizTitle, setQuizTitle] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({
    question: "",
    options: ["", "", "", ""],
    correctAnswer: 0,
    timeLimit: 20,
    points: "Standard",
  });

  useEffect(() => {
    setRoomCode(Math.floor(10000 + Math.random() * 90000));
  }, []);

  const handleQuestionChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, question: value });
  };

  const handleOptionChange = (index, value) => {
    let updatedOptions = [...currentQuestion.options];
    updatedOptions[index] = value;
    setCurrentQuestion({ ...currentQuestion, options: updatedOptions });
  };

  const addQuestion = () => {
    setQuestions([...questions, { ...currentQuestion }]);
    setCurrentQuestion({ question: "", options: ["", "", "", ""], correctAnswer: 0, timeLimit: 20, points: "Standard" });
  };

  const saveQuiz = async () => {
    const quizData = { title: quizTitle, roomCode, questions };
    try {
      await axios.post("http://localhost:5000/api/quizzes", quizData);
      alert("Quiz saved successfully!");
    } catch (error) {
      console.error("Error saving quiz:", error);
    }
  };

  return (
    <div className="admin-container">
      {/* Navbar */}
      <nav className="navbar">
        <img src={logo} alt="Logo" className="logo" />
        <input type="text" placeholder="Enter Quiz Title" value={quizTitle} className="quiz-title" onChange={(e) => setQuizTitle(e.target.value)} />
        <button className="login-btn">Login</button>
      </nav>

      {/* Quiz Section */}
      <div className="quiz-builder">
        <div className="quiz-content">
          <h2>Room Code: {roomCode}</h2>
          <input type="text" placeholder="Enter your question" value={currentQuestion.question} onChange={(e) => handleQuestionChange(e.target.value)} />

          {/* Options */}
          <div className="options-container">
            {currentQuestion.options.map((option, index) => (
              <div key={index} className="option">
                <input type="text" placeholder={`Option ${index + 1}`} value={option} onChange={(e) => handleOptionChange(index, e.target.value)} />
              </div>
            ))}
          </div>
        </div>

        {/* Right Side Settings */}
        <div className="settings">
          <button onClick={addQuestion}>Add Question</button>
          <button className="save-btn" onClick={saveQuiz}>Save Quiz</button>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
