import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, googleProvider, signInWithPopup } from "../firebase";
import "../styles/Auth.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      navigate("/username");
    } catch (error) {
      console.error("Google Sign-In Error:", error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Firebase Authentication logic here
    navigate("/username");
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="auth-title">Login</h2>
        <input type="email" className="auth-input" placeholder="Enter your email" />
        <input type="password" className="auth-input" placeholder="Enter your password" />
        <div className="button-group">
          <button className="auth-button">Login</button>
          <button className="google-button">Sign in with Google</button>
        </div>
        <p className="auth-link">Don't have an account? <a href="/register">Register</a></p>
      </div>
    </div>
  );
};

export default Login;
