import React, { useState, useEffect } from "react";
import { useNavigate, useParams , useLocation} from "react-router-dom";
import axios from "axios";
import "../styles/QuizPage.css";

const QuizPage = () => {
  const { roomNo } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  
  // ✅ Correctly track which question we are on
  const currentQuestionIndex = location.state?.currentQuestion || 0; 

  const [quiz, setQuiz] = useState(null);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [timer, setTimer] = useState(10);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchQuiz = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/quizzes/room/${roomNo}`);
        setQuiz(response.data);
        setSelectedAnswers(Array(response.data.questions.length).fill(null));
        setLoading(false);
      } catch (err) {
        console.error("❌ Error fetching quiz:", err);
        setError("Quiz not found.");
        setLoading(false);
      }
    };
    fetchQuiz();
  }, [roomNo]);

  useEffect(() => {
    if (!quiz) return;

    const countdown = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

    // ✅ When timer runs out, go to leaderboard
    if (timer === 0) {
      setTimeout(() => {
        navigate(`/leaderboard/${roomNo}`, { 
          state: { 
            currentQuestion: currentQuestionIndex, 
            totalQuestions: quiz.questions.length
          }
        });
      }, 1000);
    }

    return () => clearInterval(countdown);
  }, [timer, quiz, navigate, currentQuestionIndex, roomNo]);

  // ✅ Handle answer selection but DO NOT navigate immediately
  const handleAnswerSelection = async (index) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[currentQuestionIndex] = index;
    setSelectedAnswers(newAnswers);

    const correctAnswerIndex = quiz.questions[currentQuestionIndex].options.indexOf(
      quiz.questions[currentQuestionIndex].answer // ✅ Ensure this matches your DB format
    );

    // ✅ Calculate score but DO NOT navigate
    const isCorrect = correctAnswerIndex === index;
    const score = isCorrect ? 100 : 0;

    try {
      await axios.post("http://localhost:5000/api/quizzes/submit-score", {
        roomId: roomNo,
        username: localStorage.getItem("username"),
        score: score,
      });
      console.log("✅ Score submitted!");
    } catch (error) {
      console.error("❌ Error submitting score:", error);
    }
  };

  if (loading) return <h2>Loading quiz...</h2>;
  if (error) return <h2>{error}</h2>;

  return (
    <div className="quiz-container">
      <h1 className="quiz-title">{quiz.quizTitle} (Room: {roomNo})</h1>
      <h2 className="quiz-question">{quiz.questions[currentQuestionIndex].question}</h2>
      <div className="quiz-timer">⏳ {timer}s</div>

      <div className="quiz-options-grid">
        {quiz.questions[currentQuestionIndex].options.map((option, index) => (
          <div
            key={index}
            className={`quiz-option kahoot-color-${index} ${selectedAnswers[currentQuestionIndex] === index ? "selected" : ""}`}
            onClick={() => handleAnswerSelection(index)}
          >
            {option}
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizPage;
