import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/LeaderboardPage.css";

const LeaderboardPage = ({ scores, goToNextQuestion }) => {
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      goToNextQuestion();
    }, 5000);

    return () => clearTimeout(timer);
  }, [goToNextQuestion]);

  // Sort scores and show only top 5 players
  const topPlayers = [...scores].sort((a, b) => b.score - a.score).slice(0, 5);

  return (
    <div className="leaderboard-container">
      <h2 className="leaderboard-title">Scoreboard</h2>
      <div className="leaderboard">
        {topPlayers.map((player, index) => (
          <div key={player.name} className={`leaderboard-item rank-${index + 1}`}>
            <span className="player-name">{player.name}</span>
            <span className="player-score">{player.score} â™ </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LeaderboardPage;
