import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaUserCircle } from "react-icons/fa";

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    username: "",
    email: "",
    profilePicture: "",
    quizCount: 0,
  });

  const [newUsername, setNewUsername] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("❌ No authentication token found.");
          return;
        }

        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log("✅ Profile Data:", response.data);
        setProfile(response.data);
        setNewUsername(response.data.username);
      } catch (error) {
        console.error("❌ Error fetching profile:", error.response?.data || error.message);
      }
    };

    fetchProfile();
  }, []);

  const handleUsernameUpdate = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("❌ No authentication token found.");
        return;
      }

      const response = await axios.put(
        "http://localhost:5000/api/user/update-profile",
        { username: newUsername },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setProfile({ ...profile, username: response.data.user.username });
      setSuccess("✅ Username updated successfully!");
    } catch (error) {
      console.error("❌ Error updating username:", error.response?.data || error.message);
      setError("Failed to update username.");
    }
  };

  const handleImageUpload = async (event) => {
    if (!event.target.files || event.target.files.length === 0) {
      setError("❌ Please select an image to upload.");
      return;
    }

    const file = event.target.files[0];
    const formData = new FormData();
    formData.append("profilePicture", file);

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("❌ No authentication token found.");
        return;
      }

      const response = await axios.post(
        "http://localhost:5000/api/user/upload-profile-picture",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`, 
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setProfile({ ...profile, profilePicture: response.data.profilePicture });
      setSuccess("✅ Profile picture updated successfully!");
    } catch (error) {
      console.error("❌ Error uploading profile picture:", error.response?.data || error.message);
      setError("Failed to upload profile picture.");
    }
  };

  const handleChangePassword = async () => {
    if (newPassword !== confirmPassword) {
      setError("❌ Passwords do not match.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("❌ No authentication token found.");
        return;
      }

      const response = await axios.put(
        "http://localhost:5000/api/user/change-password",
        { oldPassword, newPassword },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess(response.data.message);
    } catch (error) {
      console.error("❌ Error changing password:", error.response?.data || error.message);
      setError("Failed to change password.");
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <label htmlFor="profile-upload" className="profile-avatar">
          {profile.profilePicture ? (
            <img src={`http://localhost:5000/uploads/${profile.profilePicture}`} alt="Profile" />
          ) : (
            <FaUserCircle size={120} />
          )}
        </label>
        <input 
          type="file" 
          id="profile-upload" 
          accept="image/*" 
          onChange={handleImageUpload} 
        />

        <h2 className="profile-username">{profile.username}</h2>
        <p className="profile-email">{profile.email}</p>
        <p className="profile-quizzes">Quizzes Created: {profile.quizCount}</p>

        <input 
          type="text" 
          className="profile-input" 
          value={newUsername} 
          onChange={(e) => setNewUsername(e.target.value)} 
          placeholder="New Username" 
        />
        <button className="profile-button" onClick={handleUsernameUpdate}>Update Username</button>

        <h3>Change Password</h3>
        <input 
          type="password" 
          className="profile-input" 
          placeholder="Old Password" 
          onChange={(e) => setOldPassword(e.target.value)} 
        />
        <input 
          type="password" 
          className="profile-input" 
          placeholder="New Password" 
          onChange={(e) => setNewPassword(e.target.value)} 
        />
        <input 
          type="password" 
          className="profile-input" 
          placeholder="Confirm Password" 
          onChange={(e) => setConfirmPassword(e.target.value)} 
        />
        <button className="profile-button" onClick={handleChangePassword}>Change Password</button>

        <button className="logout-button" onClick={() => localStorage.removeItem("token")}>Logout</button>
      </div>
    </div>
  );
};

export default ProfilePage;
