import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../styles/ResultsPage.css"; // External styling

const ResultsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // ‚úÖ Ensure scores is always an array
  const scores = Array.isArray(location.state?.scores) ? location.state.scores : [];

  // Sort scores in descending order and take the top 5
  const topWinners = [...scores].sort((a, b) => b.score - a.score).slice(0, 5);

  return (
    <div className="results-container">
      <h1>üèÜ Top 5 Winners üèÜ</h1>

      <div className="leaderboard">
        {topWinners.map((player, index) => (
          <div key={index} className={`player ${index === 0 ? "winner" : ""}`}>
            <span className="rank">#{index + 1}</span>
            <span className="name">{player.name}</span>
            <span className="score">{player.score} pts</span>
          </div>
        ))}
      </div>

      <button className="restart-btn" onClick={() => navigate("/")}>
        Play Again üîÑ
      </button>
    </div>
  );
};

export default ResultsPage;
