import { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";

const socket = io("http://localhost:5000"); // Adjust backend URL if necessary

const Admin = () => {
  const [quiz, setQuiz] = useState(null);
  const roomCode = 12345;

  useEffect(() => {
    axios.get(`http://localhost:5000/api/quiz/${roomCode}`).then((res) => {
      setQuiz(res.data);
    });

    socket.on("quizStarted", () => {
      alert("Quiz Started!");
    });

    return () => socket.off("quizStarted");
  }, []);

  const startQuiz = () => socket.emit("startQuiz", roomCode);

  return (
    <div className="p-5">
      <h1 className="text-2xl">Admin Panel</h1>
      <button onClick={startQuiz} className="bg-blue-500 text-white p-2">
        Start Quiz
      </button>
    </div>
  );
};

export default Admin;
