import { useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import img1 from "../images/create.png";
import img2 from "../images/join.png";

export default function Home() {
  const navigate = useNavigate();

  useEffect(() => {
    document.body.style.background = "linear-gradient(135deg, #1a1a40, #4b0082)";
    document.body.style.minHeight = "100vh";
    return () => {
      document.body.style.background = "";
      document.body.style.minHeight = "";
    };
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen text-white relative">
      <h1 className="text-4xl font-extrabold mb-6">Quiz Wizard</h1>
      <div className="absolute top-4 right-4">
        <button className="px-4 py-2 bg-white text-indigo-600 rounded-lg font-bold shadow-lg hover:bg-gray-200 transition">Login</button>
      </div>
      <div className="flex gap-8 mt-10">
        <Card imageUrl={img1} onClick={() => navigate('/admin')} />
        <Card imageUrl={img2} onClick={() => navigate('/join-quiz')} />
      </div>
    </div>
  );
}

function Card({ imageUrl, onClick }) {
  return (
    <motion.div
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      className="bg-white text-indigo-700 rounded-2xl shadow-xl overflow-hidden w-80 h-60 cursor-pointer hover:shadow-2xl transition flex items-center justify-center"
      onClick={onClick}
    >
      <img src={imageUrl} alt="Card" className="w-full h-full object-cover" />
    </motion.div>
  );
}
