import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/ProfilePage.css";
import { FaUserCircle } from "react-icons/fa"; 

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    name: "",
    username: "",
    email: "",
    profilePicture: "",
    quizCount: 0,
  });

  const [newUsername, setNewUsername] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setProfile(response.data);
        setNewUsername(response.data.username);
      } catch (error) {
        console.error("❌ Error fetching profile:", error);
        setError("Failed to fetch profile.");
      }
    };

    fetchProfile();
  }, []);

  return (
    <div className="profile-container">
      <div className="profile-card">
        <label htmlFor="profile-upload" className="profile-avatar">
          {profile.profilePicture ? (
            <img src={profile.profilePicture} alt="Profile" />
          ) : (
            <FaUserCircle size={120} />
          )}
        </label>
        <input type="file" id="profile-upload" accept="image/*" />

        <h2 className="profile-username">{profile.username}</h2>
        <p className="profile-email">{profile.email}</p>
        <p className="profile-quizzes">Quizzes Created: {profile.quizCount}</p>

        <input type="text" className="profile-input" value={newUsername} onChange={(e) => setNewUsername(e.target.value)} placeholder="New Username" />
        <button className="profile-button">Update Username</button>

        <h3>Change Password</h3>
        <input type="password" className="profile-input" placeholder="Old Password" />
        <input type="password" className="profile-input" placeholder="New Password" />
        <input type="password" className="profile-input" placeholder="Confirm Password" />
        <button className="profile-button">Change Password</button>

        <button className="logout-button">Logout</button>
      </div>
    </div>
  );
};

export default ProfilePage;
