import { useEffect } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import img1 from "../images/createquiz.png";
import img2 from "../images/joinquiz.png";

export default function Home() {
  useEffect(() => {
    document.body.style.background = "linear-gradient(135deg, #1a1a40, #4b0082)";
    document.body.style.minHeight = "100vh";
    return () => {
      document.body.style.background = "";
      document.body.style.minHeight = "";
    };
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen text-white relative">
      <h1 className="text-4xl font-extrabold mb-6">Quiz Wizard</h1>
      <div className="absolute top-4 right-4">
        <button className="px-4 py-2 bg-white text-indigo-600 rounded-lg font-bold shadow-lg hover:bg-gray-200 transition">Login</button>
      </div>
      <div className="flex gap-8">
        <Card imageUrl={img1} text="CREATE QUIZ" link="/create" />
        <Card imageUrl={img2} text="JOIN QUIZ" link="/join" />
      </div>
    </div>
  );
}

function Card({ imageUrl, text, link }) {
  return (
    <motion.div
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      className="bg-white text-indigo-700 rounded-2xl shadow-xl overflow-hidden w-64 cursor-pointer hover:shadow-2xl transition"
    >
      <Link to={link} className="block">
        <img src={imageUrl} alt={text} className="w-full h-40 object-cover" />
        <div className="p-4 text-center text-lg font-bold">{text}</div>
      </Link>
    </motion.div>
  );
}
