import React, { useState, useEffect } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import "../styles/QuizPage.css";

const sampleQuiz = {
  title: "JavaScript Basics",
  questions: [
    {
      question: "What is the output of `typeof null` in JavaScript?",
      options: ["Object", "Null", "Undefined", "Number"],
      correctAnswer: 0,
    },
    {
      question: "Which keyword is used to declare a constant variable?",
      options: ["var", "let", "const", "static"],
      correctAnswer: 2,
    },
    {
      question: "Which of the following is NOT a JavaScript framework?",
      options: ["React", "Vue", "Django", "Angular"],
      correctAnswer: 2,
    },
  ],
};

const QuizPage = () => {
  const { roomNo } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const currentQuestionIndex = location.state?.currentQuestion || 0;
  const [selectedAnswers, setSelectedAnswers] = useState(Array(sampleQuiz.questions.length).fill(null));
  const [timer, setTimer] = useState(10);
  const [showLeaderboard, setShowLeaderboard] = useState(false);

  useEffect(() => {
    if (timer === 0) {
      setShowLeaderboard(true);
      setTimeout(() => {
        if (currentQuestionIndex < sampleQuiz.questions.length - 1) {
          navigate(`/leaderboard/${roomNo}`, { state: { currentQuestion: currentQuestionIndex } });
        }
      }, 1000);
    }

    const countdown = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(countdown);
  }, [timer, currentQuestionIndex, navigate, roomNo]);

  const handleAnswerSelection = (index) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[currentQuestionIndex] = index;
    setSelectedAnswers(newAnswers);
  };

  return (
    <div className="quiz-container">
      <h1 className="quiz-title">{sampleQuiz.title} (Room: {roomNo})</h1>
      <h2 className="quiz-question">{sampleQuiz.questions[currentQuestionIndex].question}</h2>
      <div className="quiz-timer">‚è≥ {timer}s</div>

      <div className="quiz-options-grid">
        {sampleQuiz.questions[currentQuestionIndex].options.map((option, index) => (
          <div
            key={index}
            className={`quiz-option kahoot-color-${index} ${selectedAnswers[currentQuestionIndex] === index ? "selected" : ""}`}
            onClick={() => handleAnswerSelection(index)}
          >
            {option}
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizPage;
