import React, { useEffect, useState } from "react";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import axios from "axios";
import "../styles/LeaderboardPage.css";

const Leaderboard = () => {
  const navigate = useNavigate();
  const { roomNo } = useParams();
  const location = useLocation();
  const currentQuestionIndex = location.state?.currentQuestion || 0;
  const [leaderboard, setLeaderboard] = useState([]);
  const [timer, setTimer] = useState(7);
  const totalQuestions = location.state?.totalQuestions || 1;; // Replace with dynamic total question count if available

  // ‚úÖ Fetch Leaderboard Data
  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/quizzes/leaderboard/${roomNo}`);
        setLeaderboard(response.data);
      } catch (error) {
        console.error("‚ùå Error fetching leaderboard:", error);
      }
    };

    fetchLeaderboard();
  }, [roomNo]);

  // ‚úÖ Timer to Move to Next Question or End Quiz
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

