      if (currentQuestionIndex < quiz.questions.length - 1) {
        navigate(`/quiz/${roomNo}`, { state: { currentQuestion: currentQuestionIndex + 1 } });
      } else {
        alert("üéâ Quiz Finished! Showing Final Leaderboard.");
      }
    }, 7000);

    return () => clearInterval(countdown);
  }, [navigate, currentQuestionIndex, roomNo]);

  return (
    <div className="leaderboard-background">
      <div className="leaderboard-container">
        <h2 className="leaderboard-title">Leaderboard (Room: {roomNo})</h2>
        <div className="leaderboard-list">
          {leaderboard.length > 0 ? (
            leaderboard.map((player, index) => (
              <div key={index} className="leaderboard-item">
                <span>{player.username}</span> <strong>{player.score} pts</strong>
              </div>
            ))
          ) : (
            <p>No players yet.</p>
          )}
        </div>

        <p className="leaderboard-info">Next question in ‚è≥ {timer}s...</p>
      </div>
    </div>
  );
};

export default Leaderboard;
