import React, { useState } from "react";
import "../styles/QuizPage.css";

const sampleQuiz = {
  title: "JavaScript Basics",
  questions: [
    {
      question: "What is the output of `typeof null` in JavaScript?",
      options: ["Object", "Null", "Undefined", "Number"],
      correctAnswer: 0,
    },
    {
      question: "Which keyword is used to declare a constant variable?",
      options: ["var", "let", "const", "static"],
      correctAnswer: 2,
    },
    {
      question: "Which of the following is NOT a JavaScript framework?",
      options: ["React", "Vue", "Django", "Angular"],
      correctAnswer: 2,
    },
  ],
};

const QuizPage = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState(Array(sampleQuiz.questions.length).fill(null));
  const [submitted, setSubmitted] = useState(false);

  const handleAnswerSelection = (index) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[currentQuestion] = index;
    setSelectedAnswers(newAnswers);
  };

  const handleNext = () => {
    if (currentQuestion < sampleQuiz.questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
  };

  const handleSubmit = () => {
    setSubmitted(true);
    alert("Quiz Submitted!");
    console.log("User Answers:", selectedAnswers);
  };

  return (
    <div className="quiz-container">
      <h1 className="quiz-title">{sampleQuiz.title}</h1>
      
      <div className="quiz-card">
        <h2 className="quiz-question">{sampleQuiz.questions[currentQuestion].question}</h2>
        
        <div className="quiz-options">
          {sampleQuiz.questions[currentQuestion].options.map((option, index) => (
            <label key={index} className={`quiz-option ${selectedAnswers[currentQuestion] === index ? "selected" : ""}`}>
              <input
                type="radio"
                name={`question-${currentQuestion}`}
                checked={selectedAnswers[currentQuestion] === index}
                onChange={() => handleAnswerSelection(index)}
              />
              {option}
            </label>
          ))}
        </div>

        <div className="quiz-controls">
          {currentQuestion > 0 && (
            <button onClick={handlePrevious} className="quiz-btn prev-btn">Previous</button>
          )}
          {currentQuestion < sampleQuiz.questions.length - 1 ? (
            <button onClick={handleNext} className="quiz-btn next-btn">Next</button>
          ) : (
            <button onClick={handleSubmit} className="quiz-btn submit-btn">Submit</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default QuizPage;
