import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/AdminPanel.css";
import logo from "../images/krystal.png";
import { FaUserCircle } from "react-icons/fa";

const AdminPanel = () => {
  const [quizTitle, setQuizTitle] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [questions, setQuestions] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login state
  const [showDropdown, setShowDropdown] = useState(false); 
  const navigate = useNavigate();

  const [currentQuestion, setCurrentQuestion] = useState({
    question: "",
    options: ["", "", "", ""],
    correctAnswer: 0,
    timeLimit: 20,
    points: "Standard",
  });

  useEffect(() => {
    setRoomCode(Math.floor(10000 + Math.random() * 90000));
  }, []);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    setShowDropdown(false); // Close dropdown after login
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setShowDropdown(false); // Close dropdown after logout
  };

  const handleQuestionChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, question: value });
  };

  const handleOptionChange = (index, value) => {
    let updatedOptions = [...currentQuestion.options];
    updatedOptions[index] = value;
    setCurrentQuestion({ ...currentQuestion, options: updatedOptions });
  };

  const handleTimeChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, timeLimit: value });
  };

  const handlePointsChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, points: value });
  };

  const addQuestion = () => {
    setQuestions([...questions, { ...currentQuestion }]);
    setCurrentQuestion({ question: "", options: ["", "", "", ""], correctAnswer: 0, timeLimit: 20, points: "Standard" });
  };

  const saveQuiz = async () => {
    const quizData = { title: quizTitle, roomCode, questions };
    try {
      await axios.post("http://localhost:5000/api/quizzes", quizData);
      alert("Quiz saved successfully!");
    } catch (error) {
      console.error("Error saving quiz:", error);
    }
  };

  return (
    <div className="admin-container">
      {/* Navbar */}
      <nav className="navbar">
        <img src={logo} alt="Logo" className="logo" />
        <input 
        type="text" 
        placeholder="Enter Quiz Title" 
        value={quizTitle} 
        className="quiz-title" 
        onChange={(e) => setQuizTitle(e.target.value)} />
        
        {/* Profile Icon */}
        <div className="profile-container">
          <FaUserCircle className="profile-icon" onClick={toggleDropdown} />
          {showDropdown && (
            <div className="dropdown-menu">
              {isLoggedIn ? (
                <>
                  <button onClick={() => navigate("/dashboard")}>Dashboard</button>
                  <button onClick={handleLogout}>Logout</button>
                </>
              ) : (
                <>
                  <button onClick={handleLogin}>Login</button>
                  <button onClick={() => navigate("/dashboard")}>Dashboard</button>
                </>
              )}
            </div>
          )}
        </div>

      </nav>

      {/* Quiz Section */}
      <div className="quiz-builder">
        <div className="quiz-content">
          <h2>Room Code: {roomCode}</h2>
          <input type="text" placeholder="Enter your question" value={currentQuestion.question} onChange={(e) => handleQuestionChange(e.target.value)} />

          {/* Options */}
          <div className="options-container">
            {currentQuestion.options.map((option, index) => (
              <div key={index} className="option">
                <input type="text" placeholder={`Option ${index + 1}`} value={option} onChange={(e) => handleOptionChange(index, e.target.value)} />
              </div>
            ))}
          </div>

          {/* Add Question Button after all options */}
          <button className="add-question-btn" onClick={addQuestion}>Add Question</button>
        </div>

        {/* Right Side Panel */}
        <div className="settings">
          <label>Time Limit:</label>
          <select value={currentQuestion.timeLimit} onChange={(e) => handleTimeChange(e.target.value)}>
            <option value={10}>10 sec</option>
            <option value={20}>20 sec</option>
            <option value={30}>30 sec</option>
            <option value={40}>40 sec</option>
            <option value={50}>50 sec</option>
          </select>

          <label>Points:</label>
          <select value={currentQuestion.points} onChange={(e) => handlePointsChange(e.target.value)}>
            <option value="Standard">Standard</option>
            <option value="Double">Double</option>
            <option value="Triple">Triple</option>
          </select>

          {/* Save Quiz Button */}
          <button className="save-btn" onClick={saveQuiz}>Save Quiz</button>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
