import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/ProfilePage.css";
import { FaUserCircle } from "react-icons/fa"; 

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    name: "",
    username: "",
    email: "",
    profilePicture: "",
    quizCount: 0,
  });

  const [newUsername, setNewUsername] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setProfile(response.data);
        setNewUsername(response.data.username);
      } catch (error) {
        console.error("❌ Error fetching profile:", error);
        setError("Failed to fetch profile.");
      }
    };

    fetchProfile();
  }, []);

  // ✅ Handle Image Upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append("profilePicture", file);

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/user/upload-profile-picture",
        formData,
        { headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" } }
      );
      setProfile({ ...profile, profilePicture: response.data.profilePicture });
      setSuccess("Profile picture updated!");
    } catch (error) {
      console.error("❌ Error uploading profile picture:", error);
      setError("Failed to upload profile picture.");
    }
  };

  // ✅ Update Username
  const handleUpdateUsername = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        "http://localhost:5000/api/user/update-profile",
        { username: newUsername },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setProfile({ ...profile, username: newUsername });
      setSuccess("Username updated successfully!");
    } catch (error) {
      console.error("❌ Error updating username:", error);
      setError("Failed to update username.");
    }
  };

  // ✅ Change Password
  const handleChangePassword = async () => {
    if (newPassword !== confirmPassword) {
      setError("New password and confirmation do not match.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      await axios.put(
        "http://localhost:5000/api/user/change-password",
        { oldPassword, newPassword },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccess("Password changed successfully!");
    } catch (error) {
      console.error("❌ Error changing password:", error);
      setError("Failed to change password.");
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <label htmlFor="profile-upload" className="profile-avatar">
          {profile.profilePicture ? (
            <img src={`http://localhost:5000/${profile.profilePicture}`} alt="Profile" />
          ) : (
            <FaUserCircle size={120} />
          )}
        </label>
        <input type="file" id="profile-upload" accept="image/*" onChange={handleImageUpload} />

        <h2 className="profile-username">{profile.username}</h2>
        <p className="profile-email">{profile.email}</p>
        <p className="profile-quizzes">Quizzes Created: {profile.quizCount}</p>

        <input 
          type="text" 
          className="profile-input" 
          value={newUsername} 
          onChange={(e) => setNewUsername(e.target.value)} 
          placeholder="New Username" 
        />
        <button className="profile-button" onClick={handleUpdateUsername}>Update Username</button>

        <h3>Change Password</h3>
        <input 
          type="password" 
          className="profile-input" 
          placeholder="Old Password" 
          value={oldPassword}
          onChange={(e) => setOldPassword(e.target.value)}
        />
        <input 
          type="password" 
          className="profile-input" 
          placeholder="New Password" 
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <input 
          type="password" 
          className="profile-input" 
          placeholder="Confirm Password" 
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        <button className="profile-button" onClick={handleChangePassword}>Change Password</button>

        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}

        <button className="logout-button" onClick={() => localStorage.clear()}>Logout</button>
      </div>
    </div>
  );
};

export default ProfilePage;
