import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import LeaderboardPage from "./LeaderboardPage";  // Import Leaderboard
import "../styles/Quiz.css";

const QuizPage = () => {
  const { roomCode } = useParams();
  const navigate = useNavigate();
  const [timeLeft, setTimeLeft] = useState(20);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [showLeaderboard, setShowLeaderboard] = useState(false);

  const [quizData, setQuizData] = useState({
    questions: [
      { options: ["3", "4", "5", "6"] },
      { options: ["Berlin", "Madrid", "Paris", "Rome"] },
    ],
  });

  // Mock player scores (Replace with real scores)
  const [playerScores, setPlayerScores] = useState([
    { name: "Suzie", score: 904 },
    { name: "Kasia", score: 823 },
    { name: "John", score: 716 },
    { name: "Mike", score: 311 },
    { name: "Asia", score: 0 },
    { name: "Alex", score: 250 },
    { name: "Ryan", score: 150 },
  ]);

  useEffect(() => {
    if (!showLeaderboard) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev === 1) {
            setShowLeaderboard(true);
            return 20;
          }
          return prev - 1;
        });
      }, 1000);

      return () => clearInterval(timer);
    }
  }, [showLeaderboard, questionIndex]);

  const moveToNextQuestion = () => {
    if (questionIndex < quizData.questions.length - 1) {
      setShowLeaderboard(false);
      setQuestionIndex(questionIndex + 1);
      setTimeLeft(20);
    } else {
      navigate("/results"); // Redirect to results page after last question
    }
  };

  if (showLeaderboard) {
    return <LeaderboardPage scores={playerScores} goToNextQuestion={moveToNextQuestion} />;
  }

  return (
    <div style={{
      backgroundColor: "#f8f8f8",
      height: "100vh",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      position: "relative"
    }}>
      {/* Timer Above the Options */}
      <div style={{
        position: "absolute",
        top: "5%",
        left: "50%",
        transform: "translateX(-50%)"
      }}>
        <div style={{
          width: "120px",
          height: "120px",
          borderRadius: "50%",
          backgroundColor: "orange",
          color: "white",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          fontSize: "40px",
          fontWeight: "bold"
        }}>
          {timeLeft}
        </div>
      </div>
  
      {/* âœ… FORCING FULL-SCREEN WIDTH & HEIGHT GRID */}
      <div style={{
        display: "grid",
        gridTemplateColumns: "1fr 1fr",  // 2 equal columns
        gridTemplateRows: "1fr 1fr",    // 2 equal rows
        gap: "10px",
        width: "90vw",  // FULL SCREEN WIDTH
        height: "80vh",  // Almost full height (leaves space for timer)
      }}>
        {quizData.questions[questionIndex].options.map((option, index) => (
          <button key={index} style={{
            width: "100%",  // Full width of grid cell
            height: "100%", // Full height of grid cell
            fontSize: "30px",
            fontWeight: "bold",
            border: "none",
            cursor: "pointer",
            borderRadius: "10px",
            color: "white",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            backgroundColor: ["red", "blue", "yellow", "green"][index], // Dynamic colors
            color: index === 2 ? "black" : "white" // Make "Paris" readable on yellow
          }}>
            {option}
          </button>
        ))}
      </div>
    </div>
  );
  
  
};

export default QuizPage;
