import { useState } from "react";
import axios from "axios";
import socket from "../socket";

const Admin = () => {
  const [roomCode, setRoomCode] = useState("");
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([]);

  const addQuestion = () => {
    setQuestions([...questions, { questionText: "", options: ["", "", "", ""], correctAnswer: "" }]);
  };

  const handleCreateQuiz = async () => {
    await axios.post("http://localhost:5000/api/quiz", { roomCode, title, questions });
    alert("Quiz Created!");
  };

  const startQuiz = () => socket.emit("startQuiz", roomCode);

  return (
    <div className="p-5">
      <h1 className="text-2xl">Admin Panel</h1>
      
      <input className="border p-2" placeholder="Room Code" value={roomCode} onChange={(e) => setRoomCode(e.target.value)} />
      <input className="border p-2 ml-2" placeholder="Quiz Title" value={title} onChange={(e) => setTitle(e.target.value)} />
      
      <button className="bg-green-500 text-white p-2 ml-2" onClick={addQuestion}>Add Question</button>
      <button className="bg-blue-500 text-white p-2 ml-2" onClick={handleCreateQuiz}>Create Quiz</button>
      <button className="bg-red-500 text-white p-2 ml-2" onClick={startQuiz}>Start Quiz</button>

      {questions.map((q, index) => (
        <div key={index}>
          <input className="border p-2" placeholder="Question" onChange={(e) => {
            let newQuestions = [...questions];
            newQuestions[index].questionText = e.target.value;
            setQuestions(newQuestions);
          }} />
          <input className="border p-2 ml-2" placeholder="Correct Answer" onChange={(e) => {
            let newQuestions = [...questions];
            newQuestions[index].correctAnswer = e.target.value;
            setQuestions(newQuestions);
          }} />
        </div>
      ))}
    </div>
  );
};

export default Admin;
