import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/AdminPanel.css";
import logo from "../images/krystal.png";
import { FaUserCircle } from "react-icons/fa";

const AdminPanel = () => {
  const [quizTitle, setQuizTitle] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [questions, setQuestions] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login state
  const [showDropdown, setShowDropdown] = useState(false); 
  const navigate = useNavigate();

  const [currentQuestion, setCurrentQuestion] = useState({
    question: "",
    options: ["", "", "", ""],
    correctAnswer: 0,
    timeLimit: 20,
    points: "Standard",
  });

  useEffect(() => {
    setRoomCode(Math.floor(10000 + Math.random() * 90000));
  }, []);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleLogin = () => {
    navigate("/login")
    setIsLoggedIn(true);
    setShowDropdown(false); // Close dropdown after login
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setShowDropdown(false); // Close dropdown after logout
  };

  const handleQuestionChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, question: value });
  };

  const handleOptionChange = (index, value) => {
    let updatedOptions = [...currentQuestion.options];
    updatedOptions[index] = value;
    setCurrentQuestion({ ...currentQuestion, options: updatedOptions });
  };

  const handleTimeChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, timeLimit: value });
  };

  const handlePointsChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, points: value });
  };

  const addQuestion = () => {
    setQuestions([...questions, { ...currentQuestion }]);
    setCurrentQuestion({ question: "", options: ["", "", "", ""], correctAnswer: 0, timeLimit: 20, points: "Standard" });
  };

  const saveQuiz = async () => {
    const quizData = { title: quizTitle, roomCode, questions };
    try {
      await axios.post("http://localhost:5000/api/quizzes", quizData);
      alert("Quiz saved successfully!");
    } catch (error) {
      console.error("Error saving quiz:", error);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="absolute top-4 right-4">
        <button className="px-4 py-2 border rounded-full">LOGIN</button>
      </div>
      <div className="mb-8">
        <div className="border p-4">
          <h1 className="text-xl">APP LOGO</h1>
        </div>
      </div>
      <div className="flex flex-col md:flex-row items-center justify-center space-y-4 md:space-y-0 md:space-x-8">
        <div className="border p-4 flex flex-col items-center">
          <img
            src="https://placehold.co/150x150"
            alt="Placeholder image for creating a quiz"
            className="mb-4"
            width="150"
            height="150"
          />
          <button className="px-4 py-2 border rounded">CREATE QUIZ</button>
        </div>
        <div className="border p-4 flex flex-col items-center">
          <img
            src="https://placehold.co/150x150"
            alt="Placeholder image for joining a quiz"
            className="mb-4"
            width="150"
            height="150"
          />
          <button className="px-4 py-2 border rounded">JOIN QUIZ</button>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
