import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import axios from "axios";
import "../styles/Home.css";

export default function Home() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log("Stored Token:", token); // Debugging: Check if token is stored

    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    }

    if (token) {
      axios
      .get("http://localhost:5000/auth/me", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        console.log("User Data from API:", response.data);
        if (response.data.username) {
          setUser(response.data);
        } else {
          console.warn("No username found, fetching manually...");
          axios.get(`http://localhost:5000/auth/user/${response.data.userId}`)
            .then((userRes) => {
              setUser({ ...response.data, username: userRes.data.username });
            })
            .catch((err) => console.error("Error fetching username:", err));
        }
      })

    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setUser(null);
    navigate("/login");
  };

  return (
    <div className="home-page">
      {/* Debugging: Check user state in render */}
      {console.log("Current User State:", user)}

      {/* Login/Profile Section */}
      <div className="login-container">
        {user ? (
          <div className="profile-dropdown">
            <div
              className="profile-icon"
              onClick={() => setDropdownOpen(!dropdownOpen)}
            >
              <FaUserCircle size={30} />
              <span className="username">{user.username || "No Username"}</span>
            </div>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <Link to="/profile" className="dropdown-item">
                  My Profile
                </Link>
                <button onClick={handleLogout} className="dropdown-item">
                  Logout
                </button>
              </div>
            )}
          </div>
        ) : (
          <button className="login-button" onClick={() => navigate("/login")}>
            Login
          </button>
        )}
      </div>

      {/* Main Content */}
      <div className="home-content">
        <h1 className="home-title">Kahoot</h1>
        <div className="home-button-container">
          <button
            className="home-button"
            onClick={() => {
              if (!user) {
                navigate("/login");
              } else {
                navigate("/dashboard");
              }
            }}
          >
            Create Quiz
          </button>

          <button
            className="home-button"
            onClick={() => {
              if (!user) {
                navigate("/login");
              } else {
                navigate("/join-quiz");
              }
            }}
          >
            Join Quiz
          </button>
        </div>
      </div>
    </div>
  );
}
