import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "../styles/Quiz.css";

const QuizPage = () => {
  const { roomCode } = useParams();
  const [timeLeft, setTimeLeft] = useState(20); // Default timer
  const [questionIndex, setQuestionIndex] = useState(0);
  const [quizData, setQuizData] = useState({
    questions: [
      {
        question: "What is 2 + 2?",
        options: ["3", "4", "5", "6"],
      },
      {
        question: "Capital of France?",
        options: ["Berlin", "Madrid", "Paris", "Rome"],
      },
    ],
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev === 1) {
          moveToNextQuestion();
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [questionIndex]);

  const moveToNextQuestion = () => {
    if (questionIndex < quizData.questions.length - 1) {
      setQuestionIndex(questionIndex + 1);
      setTimeLeft(20); // Reset timer for next question
    }
  };

  return (
    <div className="quiz-container">
      {/* Timer Section */}
      <div className="timer">
        <div className="time-circle">{timeLeft}</div>
      </div>

      {/* Question Section */}
      <div className="question-section">
        <h2>Q{questionIndex + 1}: {quizData.questions[questionIndex].question}</h2>
      </div>

      {/* Answer Options (2x2 Grid Layout) */}
      <div className="options-container">
        {quizData.questions[questionIndex].options.map((option, index) => (
          <button key={index} className={`option option-${index + 1}`}>
            {option}
          </button>
        ))}
      </div>
    </div>
  );
};

export default QuizPage;
