import { useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import "../styles/Home.css";
import img1 from "../images/create.png";
import img2 from "../images/join.png";

export default function Home() {
  const navigate = useNavigate();

  useEffect(() => {
    document.body.classList.add("home-background");
    return () => {
      document.body.classList.remove("home-background");
    };
  }, []);

  return (
    <div className="home-container">
      <div className="home-header">
        <h1 className="home-title">Quiz Wizard</h1>
        <div className="home-login-button">
          <button className="login-button">Login</button>
        </div>
      </div>
      <div className="home-card-wrapper">
        <div className="home-card-container">
          <Card imageUrl={img1} onClick={() => navigate('/admin')} />
          <Card imageUrl={img2} onClick={() => navigate('/join-quiz')} />
        </div>
      </div>
    </div>
  );
}

function Card({ imageUrl, onClick }) {
  return (
    <motion.div
      whileHover={{ scale: 1.1, rotate: 3 }}
      whileTap={{ scale: 0.95 }}
      className="card"
      onClick={onClick}
    >
      <img src={imageUrl} alt="Card" className="card-image" />
    </motion.div>
  );
}
