import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaUserCircle } from "react-icons/fa";

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    username: "",
    email: "",
    profilePicture: "",
    quizCount: 0,
  });

  const [newUsername, setNewUsername] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setProfile(response.data);
        setNewUsername(response.data.username);
      } catch (error) {
        console.error("❌ Error fetching profile:", error);
        setError("Failed to fetch profile.");
      }
    };

    fetchProfile();
  }, []);

  // ✅ Handle Username Update
  const handleUpdateUsername = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        "http://localhost:5000/api/user/update-username",
        { username: newUsername },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setProfile({ ...profile, username: newUsername });
      setSuccess("Username updated successfully!");
    } catch (error) {
      console.error("❌ Error updating username:", error);
      setError("Failed to update username.");
    }
  };

  // ✅ Handle Image Upload
  const handleImageUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append("profilePicture", selectedFile);

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/user/upload-profile-picture",
        formData,
        { headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" } }
      );

      setProfile({ ...profile, profilePicture: response.data.profilePicture });
      setSuccess("Profile picture updated!");
    } catch (error) {
      console.error("❌ Error uploading profile picture:", error);
      setError("Failed to upload profile picture.");
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <label htmlFor="profile-upload" className="profile-avatar">
          {profile.profilePicture ? (
            <img src={`http://localhost:5000/uploads/${profile.profilePicture}`} alt="Profile" />
          ) : (
            <FaUserCircle size={120} />
          )}
        </label>
        <input type="file" id="profile-upload" accept="image/*" onChange={(e) => setSelectedFile(e.target.files[0])} />
        <button className="profile-button" onClick={handleImageUpload}>Upload Image</button>

        <h2 className="profile-username">{profile.username}</h2>
        <p className="profile-email">{profile.email}</p>
        <p className="profile-quizzes">Quizzes Created: {profile.quizCount}</p>

        <input type="text" className="profile-input" value={newUsername} onChange={(e) => setNewUsername(e.target.value)} placeholder="New Username" />
        <button className="profile-button" onClick={handleUpdateUsername}>Update Username</button>

        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
      </div>
    </div>
  );
};

export default ProfilePage;
