import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaCopy, FaShareAlt, FaTrash, FaClock } from "react-icons/fa";
import axios from "axios";
import "../styles/ViewQuizPage.css"; // Importing external CSS
import { getAuth, onAuthStateChanged } from "firebase/auth";

const ViewQuizPage = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [schedulingQuiz, setSchedulingQuiz] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const navigate = useNavigate();

  // ✅ Track authentication state
  useEffect(() => {
    const auth = getAuth();

    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (!user) {
        setError("User is not authenticated. Please log in.");
        setLoading(false);
        return;
      }

      console.log("✅ Logged-in User:", user);
      const token = await user.getIdToken();
      console.log("✅ Firebase Token:", token);

      try {
        const response = await axios.get(
          "http://localhost:5000/api/quizzes/my-quizzes",
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setQuizzes(response.data);
        setError("");
      } catch (error) {
        console.error("🚨 Error fetching quizzes:", error);
        setError(error.response?.data?.message || "Failed to fetch quizzes");
      } finally {
        setLoading(false);
      }
    });

    return () => unsubscribe();
  }, []);

  // ✅ Handle scheduling quiz with countdown
  const scheduleQuiz = (quiz) => {
    console.log("📝 Quiz Data:", quiz);
    console.log("📅 Start Time Received:", quiz.startTime);

    if (!quiz.startTime) {
      console.error("❌ Error: startTime is undefined or null!");
      return;
    }

    const now = new Date();
    const quizStartTime = new Date(quiz.startTime);

    const nowUTC = now.getTime();  // Get UTC timestamp
    const quizStartTimeUTC = quizStartTime.getTime();  // Convert quiz time to UTC timestamp
    const timeDifference = Math.floor((quizStartTimeUTC - nowUTC) / 1000); // Convert to seconds

    if (isNaN(quizStartTime)) {
      console.error("❌ Error: startTime is an Invalid Date!", quiz.startTime);
      return;
    }

    console.log(`📅 Current Time: ${now}`);
    console.log(`🎯 Parsed Quiz Start Time: ${quizStartTime}`);
    console.log(`⏳ Time Left in Seconds: ${timeDifference}`);

    if (timeDifference > 0) {
      setSchedulingQuiz(quiz._id); // ✅ Fix: Use `quiz._id`
      setTimeLeft(timeDifference);
    } else {
      navigate(`/quiz/${quiz._id}`); // ✅ Fix: Navigate using `quiz._id`
    }
  };

  useEffect(() => {
    if (schedulingQuiz && timeLeft > 0) {
      const interval = setInterval(() => {
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(interval);
            navigate(`/quiz/${schedulingQuiz}`); // ✅ Redirect to quiz when countdown ends
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);

      return () => clearInterval(interval); // ✅ Cleanup interval on unmount
    }
  }, [schedulingQuiz, timeLeft, navigate]);

  // ✅ Copy quiz link to clipboard
  const copyToClipboard = (roomCode) => {
    const quizLink = `http://localhost:3000/join-quiz?code=${roomCode}`;
    navigator.clipboard.writeText(quizLink);
    alert("Quiz link copied to clipboard!");
  };

  // ✅ Delete quiz from MongoDB
  const deleteQuiz = async (quizId) => {
    try {
      const auth = getAuth();
      const user = auth.currentUser;
      if (!user) {
        alert("You must be logged in to delete a quiz.");
        return;
      }

      const token = await user.getIdToken();

      await axios.delete(`http://localhost:5000/api/quizzes/delete/${quizId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setQuizzes(quizzes.filter((quiz) => quiz._id !== quizId));
    } catch (error) {
      console.error("🚨 Error deleting quiz:", error);
      alert("Failed to delete quiz.");
    }
  };

  return (
    <div className="quiz-container">
      <h1 className="quiz-title">Your Quizzes</h1>

      {loading && <p className="loading-message">Loading...</p>}
      {error && <p className="error-message">{error}</p>}

      {!loading && quizzes.length === 0 ? (
        <p className="no-quizzes">No quizzes available.</p>
      ) : (
        quizzes.map((quiz) => (
          <div key={quiz._id} className="quiz-card">
            <h2 className="quiz-card-title">{quiz.quizTitle}</h2>
            <p className="quiz-room-code">
              Room Code: <span>{quiz.roomId}</span>
            </p>

            <div className="quiz-actions">
              <button className="quiz-start-btn" onClick={() => navigate(`/quiz/${quiz._id}`)}>
                Start Quiz
              </button>

              <button className="quiz-schedule-btn" onClick={() => scheduleQuiz(quiz)}>
                <FaClock /> Schedule
              </button>

              <div className="quiz-icons">
                <button className="quiz-icon-btn" onClick={() => copyToClipboard(quiz.roomId)}>
                  <FaCopy className="quiz-icon" />
                </button>
                <button className="quiz-icon-btn">
                  <FaShareAlt className="quiz-icon" />
                </button>
                <button className="quiz-delete-btn" onClick={() => deleteQuiz(quiz._id)}>
                  <FaTrash className="quiz-icon" />
                </button>
              </div>

              {schedulingQuiz === quiz._id && (
                <p className="waiting-message">
                  Waiting for quiz to start at {new Date(quiz.startTime).toLocaleTimeString()}...
                </p>
              )}

            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default ViewQuizPage;
