import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../styles/LeaderboardPage.css";

const leaderboardData = [
  { name: "Alice", score: 100 },
  { name: "Bob", score: 80 },
  { name: "Charlie", score: 60 },
];

const Leaderboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const currentQuestion = location.state?.currentQuestion ?? 0;
  const totalQuestions = 3;
  const [timer, setTimer] = useState(7); // Leaderboard shown for 7 seconds

  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

    setTimeout(() => {
      if (currentQuestion < totalQuestions - 1) {
        navigate("/quiz", { state: { nextQuestion: currentQuestion + 1 } });
      }
    }, 7000); // Move to next question after 7s

    return () => clearInterval(countdown);
  }, [navigate, currentQuestion]);

  return (
    <div className="leaderboard-background">
      <div className="leaderboard-container">
        <h2 className="leaderboard-title">Leaderboard</h2>
        <div className="leaderboard-list">
          {leaderboardData.map((player, index) => (
            <div key={index} className="leaderboard-item">
              <span>{player.name}</span> <strong>{player.score} pts</strong>
            </div>
          ))}
        </div>

        <p className="leaderboard-info">Next question in ‚è≥ {timer}s...</p>
      </div>
    </div>
  );
};

export default Leaderboard;
