import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [roomCode, setRoomCode] = useState("");
  const navigate = useNavigate();

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-4xl font-bold mb-6">Kahoot Clone</h1>
      
      <div className="flex space-x-4 mb-6">
        <button className="bg-blue-500 text-white p-3 rounded" onClick={() => navigate("/login")}>
          Login
        </button>
        <button className="bg-green-500 text-white p-3 rounded" onClick={() => navigate("/register")}>
          Register
        </button>
      </div>

      <div className="bg-white p-6 rounded shadow-md text-center">
        <h2 className="text-2xl font-semibold mb-4">Join a Quiz</h2>
        <input
          type="text"
          placeholder="Enter Room Code"
          className="border p-2 w-64 rounded mb-4"
          value={roomCode}
          onChange={(e) => setRoomCode(e.target.value)}
        />
        <button className="bg-purple-500 text-white p-2 rounded w-full" onClick={() => navigate(`/quiz/${roomCode}`)}>
          Join Quiz
        </button>
      </div>
    </div>
  );
};

export default Home;
