import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/QuizPage.css";

const sampleQuiz = {
  title: "JavaScript Basics",
  questions: [
    {
      question: "What is the output of `typeof null` in JavaScript?",
      options: ["Object", "Null", "Undefined", "Number"],
      correctAnswer: 0,
    },
    {
      question: "Which keyword is used to declare a constant variable?",
      options: ["var", "let", "const", "static"],
      correctAnswer: 2,
    },
    {
      question: "Which of the following is NOT a JavaScript framework?",
      options: ["React", "Vue", "Django", "Angular"],
      correctAnswer: 2,
    },
  ],
};

const QuizPage = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState(Array(sampleQuiz.questions.length).fill(null));
  const [timer, setTimer] = useState(10); // 10 seconds per question
  const navigate = useNavigate();

  useEffect(() => {
    if (timer === 0) {
      navigate("/leaderboard"); // Move to Leaderboard
    }

    const countdown = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(countdown);
  }, [timer, navigate]);

  const handleAnswerSelection = (index) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[currentQuestion] = index;
    setSelectedAnswers(newAnswers);

    setTimeout(() => {
      navigate("/leaderboard"); // Auto-go to leaderboard after selection
    }, 500);
  };

  return (
    <div className="quiz-container">
      <h1 className="quiz-title">{sampleQuiz.title}</h1>

      <div className="quiz-timer">‚è≥ {timer}s</div>

      <div className="quiz-question">
        <h2>{sampleQuiz.questions[currentQuestion].question}</h2>
      </div>

      <div className="quiz-options-grid">
        {sampleQuiz.questions[currentQuestion].options.map((option, index) => (
          <div
            key={index}
            className={`quiz-option kahoot-color-${index} ${selectedAnswers[currentQuestion] === index ? "selected" : ""}`}
            onClick={() => handleAnswerSelection(index)}
          >
            {option}
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizPage;
