import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Home.css";

export default function Home() {
  const navigate = useNavigate();

  useEffect(() => {
    document.body.classList.add("home-background");
    return () => {
      document.body.classList.remove("home-background");
    };
  }, []);

  return (
    <div className="home-container">
      <div className="home-header">
        <h1 className="home-title">Kahoot</h1>
        <div className="home-login-button">
          <button className="login-button">Login</button>
        </div>
      </div>
      <div className="home-button-container">
        <button className="home-button" onClick={() => navigate('/dashboard')}>Create Quiz</button>
        <button className="home-button" onClick={() => navigate('/join-quiz')}>Join Quiz</button>
      </div>
    </div>
  );
}
