import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminPanel.css";
import logo from "../images/krystal.png";
import { FaUserCircle } from "react-icons/fa";


const AdminPanel = () => {
  const [quizTitle, setQuizTitle] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [questions, setQuestions] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login state
  const [showDropdown, setShowDropdown] = useState(false); // Control dropdown visibility

  useEffect(() => {
    setRoomCode(Math.floor(10000 + Math.random() * 90000));
  }, []);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    setShowDropdown(false); // Close dropdown after login
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setShowDropdown(false); // Close dropdown after logout
  };

  return (
    <div className="admin-container">
      {/* Navbar */}
      <nav className="navbar">
        <img src={logo} alt="Logo" className="logo" />
        <input
          type="text"
          placeholder="Enter Quiz Title"
          value={quizTitle}
          className="quiz-title"
          onChange={(e) => setQuizTitle(e.target.value)}
        />

        {/* Profile Icon */}
        <div className="profile-container">
          <FaUserCircle className="profile-icon" onClick={toggleDropdown} />
          {showDropdown && (
            <div className="dropdown-menu">
              {isLoggedIn ? (
                <>
                  <button onClick={() => alert("Go to Dashboard")}>Dashboard</button>
                  <button onClick={handleLogout}>Logout</button>
                </>
              ) : (
                <>
                  <button onClick={handleLogin}>Login</button>
                  <button onClick={() => alert("Go to Dashboard")}>Dashboard</button>
                </>
              )}
            </div>
          )}
        </div>
      </nav>
    </div>
  );
};

export default AdminPanel;
