import React, { useState } from "react";
import "../styles/AdminPanel.css"; // Create a CSS file for styling

const AdminPanel = () => {
  const [quizTitle, setQuizTitle] = useState("");
  const [questions, setQuestions] = useState([
    { question: "", options: ["", "", "", ""], correctAnswer: 0, timeLimit: 20 },
  ]);

  // Handle quiz title change
  const handleTitleChange = (e) => {
    setQuizTitle(e.target.value);
  };

  // Handle question input change
  const handleQuestionChange = (index, value) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index].question = value;
    setQuestions(updatedQuestions);
  };

  // Handle option change
  const handleOptionChange = (qIndex, optionIndex, value) => {
    const updatedQuestions = [...questions];
    updatedQuestions[qIndex].options[optionIndex] = value;
    setQuestions(updatedQuestions);
  };

  // Handle correct answer selection
  const handleCorrectAnswerChange = (qIndex, optionIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[qIndex].correctAnswer = optionIndex;
    setQuestions(updatedQuestions);
  };

  // Handle time limit change
  const handleTimeLimitChange = (index, value) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index].timeLimit = value;
    setQuestions(updatedQuestions);
  };

  // Add a new question
  const addQuestion = () => {
    setQuestions([...questions, { question: "", options: ["", "", "", ""], correctAnswer: 0, timeLimit: 20 }]);
  };

  return (
    <div className="admin-panel">
      <input
        type="text"
        placeholder="Enter quiz title..."
        value={quizTitle}
        onChange={handleTitleChange}
        className="quiz-title-input"
      />

      {questions.map((q, qIndex) => (
        <div key={qIndex} className="question-container">
          <input
            type="text"
            placeholder="Enter your question..."
            value={q.question}
            onChange={(e) => handleQuestionChange(qIndex, e.target.value)}
            className="question-input"
          />

          <div className="options-container">
            {q.options.map((option, optionIndex) => (
              <div key={optionIndex} className="option">
                <input
                  type="text"
                  placeholder={`Option ${optionIndex + 1}`}
                  value={option}
                  onChange={(e) => handleOptionChange(qIndex, optionIndex, e.target.value)}
                />
                <input
                  type="radio"
                  name={`correct-${qIndex}`}
                  checked={q.correctAnswer === optionIndex}
                  onChange={() => handleCorrectAnswerChange(qIndex, optionIndex)}
                />
              </div>
            ))}
          </div>

          <label>Time Limit:</label>
          <select value={q.timeLimit} onChange={(e) => handleTimeLimitChange(qIndex, e.target.value)}>
            <option value={10}>10 seconds</option>
            <option value={20}>20 seconds</option>
            <option value={30}>30 seconds</option>
          </select>
        </div>
      ))}

      <button onClick={addQuestion} className="add-question-btn">Add Question</button>
      <button className="save-quiz-btn">Save Quiz</button>
    </div>
  );
};

export default AdminPanel;
