import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CreateQuiz.css";

export default function CreateQuiz() {
  const [quizTitle, setQuizTitle] = useState("");
  const [questions, setQuestions] = useState([
    { question: "", options: ["", "", "", ""], answer: "", time: 30, points: 100 },
  ]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }
  }, []);

  const handleQuestionChange = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].question = value;
    setQuestions(newQuestions);
  };

  const handleOptionChange = (index, optionIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[index].options[optionIndex] = value;
    setQuestions(newQuestions);
  };

  const handleAnswerChange = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].answer = value;
    setQuestions(newQuestions);
  };

  const handleTimeChange = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].time = value;
    setQuestions(newQuestions);
  };

  const handlePointsChange = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].points = value;
    setQuestions(newQuestions);
  };

  const addQuestion = () => {
    setQuestions([...questions, { question: "", options: ["", "", "", ""], answer: "", time: 30, points: 100 }]);
  };

  const handleSubmit = async () => {
    if (!quizTitle || questions.some(q => !q.question || !q.answer)) {
      setError("Please fill in all questions and answers.");
      return;
    }
    
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/quizzes/create",
        { quizTitle, questions },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess("Quiz created successfully!");
      console.log("✅ Quiz created:", response.data);
    } catch (error) {
      console.error("❌ Quiz creation error:", error.response?.data || error.message);
      setError("Failed to create quiz. Please try again.");
    }
  };

  return (
    <div className="create-quiz-container">
      <h1 className="create-quiz-title">Create Your Quiz</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}
      
      <div className="quiz-content">
        <div className="quiz-form">
          <input
            type="text"
            className="quiz-title-input"
            placeholder="Enter Quiz Title"
            value={quizTitle}
            onChange={(e) => setQuizTitle(e.target.value)}
          />

          {questions.map((question, index) => (
            <div key={index} className="question-container">
              <input
                type="text"
                className="question-input"
                placeholder={`Question ${index + 1}`}
                value={question.question}
                onChange={(e) => handleQuestionChange(index, e.target.value)}
              />
              {question.options.map((option, optionIndex) => (
                <input
                  key={optionIndex}
                  type="text"
                  className="option-input"
                  placeholder={`Option ${optionIndex + 1}`}
                  value={option}
                  onChange={(e) => handleOptionChange(index, optionIndex, e.target.value)}
                />
              ))}
              <input
                type="text"
                className="answer-input"
                placeholder="Correct Answer"
                value={question.answer}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
              />
            </div>
          ))}

          <button className="add-question-button" onClick={addQuestion}>Add Question</button>
          <button className="submit-quiz-button" onClick={handleSubmit}>Submit Quiz</button>
        </div>

        {/* Side Panel */}
        <div className="side-panel">
          {questions.map((question, index) => (
            <div key={index} className="settings-container">
              <label>Time Limit:</label>
              <select
                value={question.time}
                onChange={(e) => handleTimeChange(index, parseInt(e.target.value))}
              >
                {[10, 20, 30, 60].map((time) => (
                  <option key={time} value={time}>{time} seconds</option>
                ))}
              </select>
              
              <label>Points:</label>
              <select
                value={question.points}
                onChange={(e) => handlePointsChange(index, parseInt(e.target.value))}
              >
                {[50, 100, 200, 500].map((points) => (
                  <option key={points} value={points}>{points} points</option>
                ))}
              </select>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
