import React, { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";  // Generate room codes
import "../styles/AdminPanel.css";
import axios from "axios";
import logo from "../images/krystal.png"

const AdminPanel = () => {
  const [quizTitle, setQuizTitle] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({
    question: "",
    options: ["", "", "", ""],
    correctAnswer: 0,
    timeLimit: 20,
    points: "Standard",
  });

  // Generate unique 5-digit code
  useEffect(() => {
    setRoomCode(Math.floor(10000 + Math.random() * 90000)); 
  }, []);

  // Handle question changes
  const handleQuestionChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, question: value });
  };

  const handleOptionChange = (index, value) => {
    let updatedOptions = [...currentQuestion.options];
    updatedOptions[index] = value;
    setCurrentQuestion({ ...currentQuestion, options: updatedOptions });
  };

  const handleCorrectAnswerChange = (index) => {
    setCurrentQuestion({ ...currentQuestion, correctAnswer: index });
  };

  const handleTimeLimitChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, timeLimit: value });
  };

  const handlePointsChange = (value) => {
    setCurrentQuestion({ ...currentQuestion, points: value });
  };

  // Add question
  const addQuestion = () => {
    setQuestions([...questions, { ...currentQuestion }]);
    setCurrentQuestion({ question: "", options: ["", "", "", ""], correctAnswer: 0, timeLimit: 20, points: "Standard" });
  };

  // Save Quiz to MongoDB
  const saveQuiz = async () => {
    const quizData = {
      title: quizTitle,
      roomCode,
      questions,
    };

    try {
      await axios.post("http://localhost:5000/api/quizzes", quizData);
      alert("Quiz saved successfully!");
    } catch (error) {
      console.error("Error saving quiz:", error);
    }
  };

  return (
    <div className="admin-container">
      {/* Navbar */}
      <nav className="navbar">
        <img src={logo} alt="Kahoot Logo" className="logo" />
        <input type="text" placeholder="Enter Quiz Title" value={quizTitle} onChange={(e) => setQuizTitle(e.target.value)} />
        <button className="login-btn">Login</button>
      </nav>

      {/* Main Content */}
      <div className="quiz-builder">
        <div className="quiz-content">
          <h2>Room Code: {roomCode}</h2>
          <input type="text" placeholder="Enter your question" value={currentQuestion.question} onChange={(e) => handleQuestionChange(e.target.value)} />

          {/* Options */}
          <div className="options-container">
            {currentQuestion.options.map((option, index) => (
              <div key={index} className="option">
                <input type="text" placeholder={`Option ${index + 1}`} value={option} onChange={(e) => handleOptionChange(index, e.target.value)} />
                <input type="radio" name="correctAnswer" checked={currentQuestion.correctAnswer === index} onChange={() => handleCorrectAnswerChange(index)} />
              </div>
            ))}
          </div>

          {/* Settings */}
          <div className="settings">
            <label>Time Limit:</label>
            <select value={currentQuestion.timeLimit} onChange={(e) => handleTimeLimitChange(e.target.value)}>
              <option value={10}>10 seconds</option>
              <option value={20}>20 seconds</option>
              <option value={30}>30 seconds</option>
            </select>

            <label>Points:</label>
            <select value={currentQuestion.points} onChange={(e) => handlePointsChange(e.target.value)}>
              <option value="Standard">Standard</option>
              <option value="Double">Double</option>
            </select>
          </div>

          {/* Buttons */}
          <button onClick={addQuestion}>Add Question</button>
        </div>

        {/* Sidebar - Display Added Questions */}
        <div className="question-sidebar">
          <h3>Added Questions</h3>
          {questions.map((q, index) => (
            <div key={index} className="question-slide">
              {q.question}
            </div>
          ))}
        </div>
      </div>

      {/* Save Button */}
      <button className="save-btn" onClick={saveQuiz}>Save Quiz</button>
    </div>
  );
};

export default AdminPanel;
