import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "../styles/QuizPage.css";

const QuizPage = () => {
  const { roomNo } = useParams(); // Get room number from URL
  console.log("üÜî Received roomNo from URL:", roomNo);
  const navigate = useNavigate();

  const [quiz, setQuiz] = useState(null); // Store fetched quiz data
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [timer, setTimer] = useState(10);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ‚úÖ Fetch quiz from backend when component loads
  useEffect(() => {
    console.log("üÜî Received roomNo from URL:", roomNo);
    if (!roomNo) {
      console.error("‚ùå Error: roomNo is undefined!");
      setError("Invalid quiz room. Please check the link.");
      return;
    }
    const fetchQuiz = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/quizzes/room/${roomNo}`);
        setQuiz(response.data); // Store quiz data
        setSelectedAnswers(Array(response.data.questions.length).fill(null));
        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error fetching quiz:", err);
        setError("Quiz not found or has been removed.");
        setLoading(false);
      }
    };

    fetchQuiz();
  }, [roomNo]);

  // ‚úÖ Timer for each question
  useEffect(() => {
    if (!quiz) return;

    if (timer === 0) {
      if (currentQuestionIndex < quiz.questions.length - 1) {
        setCurrentQuestionIndex((prev) => prev + 1);
        setTimer(10); // Reset timer for next question
      } else {
        navigate(`/leaderboard/${roomNo}`);
      }
    }

    const countdown = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(countdown);
  }, [timer, currentQuestionIndex, quiz, navigate, roomNo]);

  // ‚úÖ Handle answer selection
  const handleAnswerSelection = (index) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[currentQuestionIndex] = index;
    setSelectedAnswers(newAnswers);
  };

  // ‚úÖ Handle loading state
  if (loading) return <h2>Loading quiz...</h2>;
  if (error) return <h2>{error}</h2>;

  return (
    <div className="quiz-container">
      <h1 className="quiz-title">{quiz.quizTitle} (Room: {roomNo})</h1>
      <h2 className="quiz-question">{quiz.questions[currentQuestionIndex].question}</h2>
      <div className="quiz-timer">‚è≥ {timer}s</div>

      <div className="quiz-options-grid">
        {quiz.questions[currentQuestionIndex].options.map((option, index) => (
          <div
            key={index}
            className={`quiz-option kahoot-color-${index} ${selectedAnswers[currentQuestionIndex] === index ? "selected" : ""}`}
            onClick={() => handleAnswerSelection(index)}
          >
            {option}
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizPage;
