import React, { useState } from "react";
import { FaCopy, FaShareAlt, FaTrash } from "react-icons/fa";
import "../styles/ViewQuizPage.css"; // Importing external CSS

const ViewQuizPage = () => {
  const [quizzes, setQuizzes] = useState([
    {
      id: 1,
      title: "Math Quiz - Basic Arithmetic",
      description: "A short quiz to test your basic arithmetic skills.",
      roomCode: "12345",
      link: "https://quizapp.com/quiz/12345",
    },
    {
      id: 2,
      title: "Science Quiz - Physics Basics",
      description: "Test your knowledge of fundamental physics concepts.",
      roomCode: "67890",
      link: "https://quizapp.com/quiz/67890",
    },
  ]);

  // Function to copy quiz link
  const copyToClipboard = (link) => {
    navigator.clipboard.writeText(link);
    alert("Quiz link copied to clipboard!");
  };

  // Function to delete quiz
  const deleteQuiz = (id) => {
    const updatedQuizzes = quizzes.filter((quiz) => quiz.id !== id);
    setQuizzes(updatedQuizzes);
  };

  return (
    <div className="quiz-container">
      <h1 className="quiz-title">Your Quizzes</h1>

      {quizzes.length === 0 ? (
        <p className="no-quizzes">No quizzes available.</p>
      ) : (
        quizzes.map((quiz) => (
          <div key={quiz.id} className="quiz-card">
            <h2 className="quiz-card-title">{quiz.title}</h2>
            <p className="quiz-room-code">Room Code: <span>{quiz.roomCode}</span></p>
            <p className="quiz-description">{quiz.description}</p>

            <div className="quiz-actions">
              <button className="quiz-start-btn">Start Quiz</button>

              <div className="quiz-icons">
                <button className="quiz-icon-btn" onClick={() => copyToClipboard(quiz.link)}>
                  <FaCopy className="quiz-icon" />
                </button>
                <button className="quiz-icon-btn">
                  <FaShareAlt className="quiz-icon" />
                </button>
                <button className="quiz-delete-btn" onClick={() => deleteQuiz(quiz.id)}>
                  <FaTrash className="quiz-icon" />
                </button>
              </div>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default ViewQuizPage;
