import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/ProfilePage.css";  

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    name: "",
    username: "",
    email: "",
    profilePicture: "",  
  });

  const [newUsername, setNewUsername] = useState("");
  const [newName, setNewName] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [profileImage, setProfileImage] = useState(null);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setProfile(response.data);
        setNewUsername(response.data.username);
        setNewName(response.data.name);
      } catch (error) {
        console.error("❌ Error fetching profile:", error);
        setError("Failed to fetch profile.");
      }
    };

    fetchProfile();
  }, []);

  const handleProfileUpdate = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        "http://localhost:5000/api/user/update-profile",
        { name: newName, username: newUsername },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess(response.data.message);
      setProfile((prev) => ({ ...prev, name: newName, username: newUsername }));
    } catch (error) {
      console.error("❌ Error updating profile:", error);
      setError("Failed to update profile.");
    }
  };

  const handlePasswordChange = async () => {
    if (newPassword !== confirmPassword) {
      setError("Passwords do not match!");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:5000/api/user/change-password",
        { oldPassword, newPassword },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess("Password updated successfully!");
      setOldPassword("");
      setNewPassword("");
      setConfirmPassword("");
    } catch (error) {
      console.error("❌ Error changing password:", error);
      setError("Failed to update password.");
    }
  };

  const handleProfilePictureUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("profilePicture", file);

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/user/upload-profile-picture",
        formData,
        { headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" } }
      );

      setProfile((prev) => ({ ...prev, profilePicture: response.data.profilePicture }));
      setSuccess("Profile picture updated!");
    } catch (error) {
      console.error("❌ Error uploading profile picture:", error);
      setError("Failed to upload profile picture.");
    }
  };

  return (
    <div className="profile-container">
      <h2>My Profile</h2>
      
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div className="profile-picture">
        <img src={profile.profilePicture || "/default-avatar.png"} alt="Profile" />
        <input type="file" accept="image/*" onChange={handleProfilePictureUpload} />
      </div>

      <div className="profile-info">
        <label>Name</label>
        <input type="text" value={newName} onChange={(e) => setNewName(e.target.value)} />

        <label>Username</label>
        <input type="text" value={newUsername} onChange={(e) => setNewUsername(e.target.value)} />

        <label>Email</label>
        <input type="text" value={profile.email} disabled />
      </div>

      <button onClick={handleProfileUpdate}>Save Changes</button>

      <h3>Change Password</h3>
      <input type="password" placeholder="Old Password" value={oldPassword} onChange={(e) => setOldPassword(e.target.value)} />
      <input type="password" placeholder="New Password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
      <input type="password" placeholder="Confirm New Password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
      <button onClick={handlePasswordChange}>Change Password</button>
    </div>
  );
};

export default ProfilePage;
